public class Pen{public enum Color{RED("red"),GREEN("green"),BLUE("blue");private final String color;Color(String color){this.color=color;}@Override public String toString(){return color;}}private Color color;private boolean cap=true;public Pen(){color=Color.RED;}public Pen(Color color){this.color=color;}public String draw(){if(cap){return"";}else{return"Drawing "+color;}}public void capOff(){this.cap=false;}public void capOn(){this.cap=true;}public void changeColor(Color color){if(cap)this.color=color;}}